package Input_Output;

public class Cat_Escape {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Escape Sequence: 백슬래시(\)+문자
		System.out.println("\\    /\\");
		System.out.println(" )  ( ')");
		System.out.println("(   / )");
		System.out.println(" \\(__)|");
		
		System.out.println();
		
		// 가독성을 더 높이기 위한 수정 with \n
		System.out.println("\\    /\\\n"+
							" )  ( ')\n"+
							"(   / )\n"+
							" \\(__)|");
	}

}
