package p2023_07_12;
// p69
// boolean 및 if문

public class VariableEx05_boolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean stop = true;
		if (stop) {		// if문 (조건식)이 true이면 실행
			System.out.println("중지합니다.");
		} else {
			System.out.println("시작합니다.");
		}
	}
}
